import React, { useState } from 'react';
import './Modal.css'; // Import styles for the modal

function AddNoteModal({ onSave, onClose }) {
  const [title, setTitle] = useState(''); // State for note title
  const [content, setContent] = useState(''); // State for note content

  // Function to handle saving the note
  const handleSave = () => {
    if (title && content) {
      onSave({ title, content }); // Call onSave prop with the new note data
      setTitle(''); // Reset the title state
      setContent(''); // Reset the content state
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Add Note</h2>
        
        {/* Title input field */}
        <label>
          Title
          <input
            type="text"
            placeholder="Enter note title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </label>

        {/* Content textarea field */}
        <label>
          Content
          <textarea
            placeholder="Enter note content...."
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
        </label>

        {/* Buttons to save or cancel */}
        <div className="modal-actions">
          <button className="save" onClick={handleSave}>Save</button>
          <button onClick={onClose} >Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default AddNoteModal;
