// src/App.js
import React from 'react';
import Header from './components/Header';
import NoteList from './components/NoteList';
import AddNoteModal from './components/AddNoteModal';
import { useNotes } from './hooks/useNotes';
import './App.css';

function App() {
  const {
    notes,
    addNote,
    deleteNote,
    editNote,
    setEditNote
  } = useNotes();

  // Handle open modal for adding or editing a note
  const openModal = () => setEditNote(null); // Opens modal for a new note

  return (
    <div className="App">
      <div className="container">
        <Header onAddNote={openModal} />
        <NoteList notes={notes} onDelete={deleteNote} onEdit={setEditNote} />
        <AddNoteModal
          isOpen={Boolean(editNote) || editNote === null} // Ensure modal opens for new or existing notes
          onRequestClose={() => setEditNote(null)}
          onSave={addNote}
          note={editNote}
        />
      </div>
    </div>
  );
}

export default App;
