import React, { useState } from 'react';
import Header from './components/Header';
import NotesList from './components/NotesList';
import AddNoteModal from './components/AddNoteModal';
import './App.css';

function App() {
  const [notes, setNotes] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Function to handle adding a new note
  const addNote = (note) => {
    setNotes([...notes, note]); // Add the new note to the list
    setIsModalOpen(false); // Close the modal
  };

  // Function to handle deleting a note
  const deleteNote = (index) => {
    const newNotes = notes.filter((_, i) => i !== index); // Remove the note from the list
    setNotes(newNotes);
  };

  return (
    <div className="container">
      {/* Header component with Add New Note button */}
      <Header onAddNote={() => setIsModalOpen(true)} />

      {/* List of notes */}
      <NotesList notes={notes} onDeleteNote={deleteNote} />

      {/* Conditional rendering for Add Note Modal */}
      {isModalOpen && (
        <AddNoteModal onSave={addNote} onClose={() => setIsModalOpen(false)} />
      )}
    </div>
  );
}

export default App;
